from PyQt5 import QtCore, QtGui, QtWidgets
import bd
import sys
from PyQt5 import uic
from bd import studgroups_add
class test(QtWidgets.QMainWindow):
    mas_zac = []
    mas_fio = []
    def __init__(self):
        #QtWidgets.QMainWindow.__init__(self)
        self.set()
    def set(self):
        self.t=uic.loadUi('adduvg(1).ui')
        self.t.pushButton_2.clicked.connect(self.pusk)
        self.t.comboBox_2.activated['int'].connect(self.check)
        self.mas_zac, self.mas_fio = bd.student()
        self.t.comboBox.addItem("")
        self.t.comboBox_2.addItem("")
        self.Ustanovka()
        self.t.show()
    def pusk(self):
        if self.t.label_2.text()!=" ":
            bd.studgroups_add(int(self.t.label_2.text()), self.t.comboBox.currentText())
            self.Ustanovka()
    def Ustanovka(self):
        self.t.label_2.setText("")
        self.t.comboBox.clear()
        self.t.comboBox_2.clear()
        mas_name = bd.groups_output()  # Проверяем набор групп
        for i in range(0, len(mas_name)):
            self.t.comboBox.addItem("")
            self.t.comboBox.setItemText(i, mas_name[i])
        for i in range(len(self.mas_fio)):
            self.t.comboBox_2.addItem("")
            self.t.comboBox_2.setItemText(i, self.mas_fio[i])
    def check(self, ind):
        self.t.label_2.setText(str(self.mas_zac[ind]))
